import React,{Component} from 'react'
import {connect} from 'react-redux'
import {getPatientAppointmentsActionCreator} from '../actions/getPatientAppointmentsActionCreator'
import {Table,Button,Container} from 'react-bootstrap'
import { Link } from 'react-router-dom';
import _ from 'lodash';


class GetPatientBookingsAppointments extends Component {

    componentDidMount(){
        console.log('authenticatedUser',this.props.authenticatedUser)
        const values={};
        values.token = this.props.authenticatedUser.token
        values._id = this.props.authenticatedUser._id
        console.log('valuesTobe',values)
        this.props.getPatientAppointmentsActionCreator(values,() =>{

        })

    }

    render(){
    console.log('authenticatedUser',this.props.authenticatedUser)
        return (
            <Container className=' mt-5'>
            <div>
                <h1>Your Appointments!</h1>
                <Table striped bordered hover>
            <thead>
              <tr>
                <th>Patient Name</th>
                <th></th>
                <th>Submitted At</th>
                <th>Assigned To</th>
                <th>Status</th>
                <th>View</th>
                <th>Edit</th>
                <th>Remove</th>
              </tr>
            </thead>

            <tbody>
              {_.map(this.props.gotApplicationSuccessfully, (application) => {
                return (
                  <tr key={application._id}>
                    <td>
                      {application.owner.firstname.concat(
                        ' ',
                        application.owner.lastname
                      )}
                    </td>
                    <td>{application.nearestMedicalFacility}</td>
                    <td>{application.createdAt}</td>
                    <td>Dr Mathuba</td>
                    <td>
                      <Button
                        variant={this.statusOfApplication(
                          application.assignedMedicalPractitioner
                        )}
                      >
                        {application.assignedMedicalPractitioner}
                      </Button>
                    </td>
                    <td>
                      <Button
                        as={Link}
                        to={`/viewUserApplication/${application._id}`}
                        variant='primary'
                      >
                        View
                      </Button>
                    </td>
                    <td>
                      <Button
                        as={Link}
                        to={`/editUserApplication/${application._id}`}
                        variant='info'
                      >
                        Edit
                      </Button>
                    </td>
                    <td>
                      <Button
                        type='button'
                        as={Link}
                        to={`/deleteUserApplication/${application._id}`}
                        variant='danger'
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </Table>
            </div>
            </Container>
        )
    }

}

function mapStateToProps(state){

    return{
        authenticatedUser:state.authReducer.authenticated
    }

}


export default (connect(mapStateToProps, { getPatientAppointmentsActionCreator })(GetPatientBookingsAppointments))
