import React from 'react';
import { Component } from 'react';
import { Button, Form } from 'react-bootstrap';
import { Container } from 'react-bootstrap';
import { Field,reduxForm } from 'redux-form';


class PersonalInfo extends Component{

    renderGenderField = ( { className, label , input , type , placeholder,name  } ) =>{

        return (
            <div className={className}>
              <Form.Group >
                <Form.Label>{label}</Form.Label>
                <Form.Check
                {...input}
                type={type}
                label="Male"
                name={name}
                id="genderOne"
                />
                <Form.Check
                {...input}
                type={type}
                label="Female"
                name={name}
                id="genderTwo"
                />

                {/* {this.renderError(meta)} */}
              </Form.Group>
            </div>
          );

    }


    renderFormFields = ({ className, label , input , type , placeholder , }) =>{ 

        return (
            <div className={className}>
              <Form.Group >
                <Form.Label>{label}</Form.Label>
                <Form.Control
                  required
                  {...input}
                  type={type}
                  placeholder={placeholder}
                />

                {/* {this.renderError(meta)} */}
              </Form.Group>
            </div>
          );
     }



    render(){

        return( 

            <div className='container mt-5 mb-5'>
                <Container>
                <h3 className='text-center text-secondary mb-5'>Basic Profile Info</h3>

                <Form>

                <Form.Row>
                    <Field 
                    name = 'firstname'
                    label="Firstname"
                    type="text"
                    placeholder="Firstname"
                    className="col-md-4"
                    component={ this.renderFormFields }   
                    />
                </Form.Row>

                <Form.Row>
                    <Field 
                    name = 'lastname'
                    label="Lastname"
                    type="text"
                    placeholder="Lastname"
                    className="col-md-4"
                    component={ this.renderFormFields  } 

                    />

                </Form.Row>

                <Form.Row>
                    <Field 
                    name = 'email'
                    label="Email"
                    type="email"
                    placeholder="Email"
                    className="col-md-4"
                    component={ this.renderFormFields } 

                    />

                </Form.Row>

                <Form.Row>


                    <Field 
                    name = 'phone'
                    label="Phone"
                    type="text"
                    placeholder="Phone"
                    className="col-md-4"
                    component={ this.renderFormFields  } 

                    />
                </Form.Row>

                <Form.Row>
                    <Field 
                    name = 'password'
                    label="Password"
                    type="password"
                    placeholder="Password"
                    className="col-md-4"
                    component={ this.renderFormFields  } 

                    />


                </Form.Row>

                <Form.Row>
                    <Field 
                    name = 'gender'
                    label="Gender"
                    type="radio"
                    placeholder="Gender"
                    className="col-md-4 mb-3"
                    component={ this.renderGenderField } 

                    />
                </Form.Row>


                <div>

                <Button
                className="mr-3"
                 type='submit'
                 variant={this.props.variant1}
                >
                Save
                </Button>

                <Button
                 type='submit'
                 variant="warning"
                >
                Cancel
                </Button>

                </div>



                </Form>


                </Container>

            </div>
         )
    }
}

export default reduxForm({
    form: 'personalInfoForm'
  })(PersonalInfo);
