import React, { Component } from 'react';
import { Form, Button } from 'react-bootstrap';
import { connect } from 'react-redux';
import { Field, reduxForm } from 'redux-form';
import { load } from '../actions/getAllDoctors';
import _ from 'lodash';

const data = {
  // used to populate "account" reducer when "Load" is clicked
  firstName: 'Jane',
  email: 'mathubar@stanbic.com',
};
var min =''

class BookAppointment extends Component {

  componentDidMount() {
    var anotherHolding = JSON.stringify(this.props.initialValues);
    // console.log('holding' + holding)
    console.log('anotherHolding' + anotherHolding);

    // console.log('holding' + this.props.dataFill)
    console.log('anotherHolding' + this.props.initialValues);

    // console.log('holding' + JSON.parse(holding))
    console.log('anotherHolding' + JSON.parse(anotherHolding));

    //   const dataa = {  // used to populate "account" reducer when "Load" is clicked
    //   firstName: 'Jane',
    //   email:'mathubar@stanbic.com',

    // }

    // this.props.load(dataa)
   min= this.renderDateHelper()
   console.log('ming ',min)
  }


renderDateHelper(){
   
    var date = new Date()
    var day = date.getDate()
    var month = date.getMonth() +1
    var year = date.getFullYear()

    var fullDate = year + '-' + month + '-' + day
     const stringDate = _.toString(fullDate)
    console.log('fullDate ',fullDate)
    console.log('StringDate ',stringDate)

    
    return stringDate

}
  
renderError({ error, touched }) {
  if (error && touched) {
    return (
      <div className=''>
        <Alert variant='danger'>{error}</Alert>
      </div>
    );
  }
}
renderHelperGender = ({
  input,
  type,
  label,
  controlId,
  className,
  placeholder,
  required,
  accept,
  meta,
  as,
  error,
  maxDate,
}) => {
  console.log('meta ', meta);

  return (
    <div className={className}>
      <Form.Group controlId={controlId}>
        <Form.Label>{label}</Form.Label>
        <Form.Control
          required
          {...input}
          type={type}
          placeholder={placeholder}
          as={as}
          //  max={maxDate}
        >
          <option>Male </option>

<option>Female</option>
        </Form.Control>

        {this.renderError(meta)}
      </Form.Group>
    </div>
  );
};

  renderHelper = ({
    input,
    type,
    disabled,
    label,
    controlId,
    className,
    placeholder,
    required,
    accept,
    meta,
    error,
    min,
  }) => {
    console.log('meta ', meta);

    return (
      <div className={className}>
        <Form.Group controlId={controlId}>
          <Form.Label>{label}</Form.Label>
          <Form.Control
            required
            {...input}
            type={type}
            placeholder={placeholder}
            disabled={disabled}
            min={min}
          />
        </Form.Group>
      </div>
    );
  };

  render() {
    const { handleSubmit } = this.props;

    return (
      <div className='container mt-5 mb-5'>
        <h3 className='text-center text-secondary mb-5'>Book Appointment</h3>

        <Form>
          <Form.Row>
            <Field
              name='Patientfirstname'
              label='Patient Name'
              component={this.renderHelper}
              type='text'
              disabled='disabled'
              required='required'
              placeholder='Patient Name'
              controlId='formGroupFirstname'
              className='col-md-4'
            />
            <Field
              name='Patientlastname'
              label='Patient Lastname'
              component={this.renderHelper}
              type='text'
              disabled='disabled'
              required='required'
              placeholder='Patient Lastname'
              controlId='formGroupFirstname'
              className='col-md-4'
            />
            {/* <div className='col-md-6'>
      
               <Form.Group>
                 <Form.Label>Patient Name</Form.Label>
                   <Form.Control 
                   type='text'
                   placeholder='Patient Name'
                   />
                 
               </Form.Group>
               </div> */}
            <Field
              name='Patientemail'
              label='Patient Email'
              component={this.renderHelper}
              disabled='disabled'
              type='text'
              required='required'
              placeholder='Patient Email'
              controlId='formGroupPatientEmail'
              className='col-md-4'
            />
          </Form.Row>

          <Form.Row>
            {/* <div className='col-md-4'>
              <Form.Group>
                <Form.Label>Category</Form.Label>
                <Form.Control as='select' placeholder='Category'>
                  <option>General Physician </option>

                  <option>Cardiologist</option>
                  <option>Neurosurgeon</option>
                  <option>Oncologist</option>
                  <option>Orthopaedic Surgeon</option>
                  <option>Neurologist</option>
                  <option>Gastroenterologist</option>
                  <option>ENT</option>
                  <option>Dentist</option>
                  <option>Psychiatrist</option>
                  <option>Urologist</option>
                  <option>Gynecologits</option>
                  <option>Pediatrician</option>
                </Form.Control>
              </Form.Group>
            </div> */}

            <Field
              name='department'
              label='Doctor Category'
              component={this.renderHelper}
              disabled='disabled'
              type='text'
              required='required'
              placeholder='Doctor Category'
              controlId='formGroupDoctorCategory'
              className='col-md-4'
            />

            <Field
              name='lastname'
              label='Doctor'
              component={this.renderHelper}
              disabled='disabled'
              type='text'
              required='required'
              placeholder='Doctor name'
              controlId='formGroupDoctorName'
              className='col-md-4'
            />
            {/* <div className='col-md-4'>
              <Form.Group>
                <Form.Label>Doctor</Form.Label>
                <Form.Control as='select' placeholder='Select Doctor'>
                  <option>Select Doctor</option>

                  <option>Dr Mathuba</option>
                  <option>Dr Mmopiemang</option>
                  <option>Dr Gure</option>
                  <option>Dr Mosepele</option>
                  <option>Dr Kang</option>
                </Form.Control>
              </Form.Group>
            </div> */}

            <div className='col-md-4'>
              <Form.Group>
                <Form.Label>Location</Form.Label>
                <Form.Control as='select' placeholder='Select Hospital'>
                  <option>Select Hospital</option>

                  <option>Kalafhi Private Hospital</option>
                  <option>Gaborone Private Hospital</option>
                  <option>Bokamoso Private Hospital</option>
                  <option>Sidilega Private Hospital</option>
                  <option>AO Private Clinic</option>
                </Form.Control>
              </Form.Group>
            </div>
          </Form.Row>
          <Form.Row>
            {/* <div className='col-md-6'>
              <Form.Group>
                <Form.Label>Appointment Date</Form.Label> */}
                <Field
                name='AppointmentDate'
              label='Appointment Date'
              type='date'
              min={min}
              component={this.renderHelper}
              required='required'
            
              controlId='formGroupAppointmentDate'
              className='col-md-6'
                 />
              {/* </Form.Group>
            </div> */}

            {/* <div className='col-md-6'> */}
              {/* <Form.Group>
                <Form.Label>Available Appointment Time</Form.Label> */}
                <Field
                name='AppointmentTime'
              label='Appointment Time'
              type='time'
              // min={min}
              component={this.renderHelper}
              required='required'
              placeholder='Appointment Time'
              controlId='formGroupAppointmentTime'
              className='col-md-6'
                />
              {/* </Form.Group> */}
            {/* </div> */}

            {/* <div className='col-md-6'>
      
               <Form.Group>
                 <Form.Label>Patient Email</Form.Label>
                   <Form.Control 
                   type='text'
                   placeholder='Patient Email'
                   />
                 
               </Form.Group>
               </div> */}
          </Form.Row>
          <Form.Row>
          <Field
                name='AppointmentTime'
              label='Appointment Time'
              type='time'
              // min={min}
              component={this.renderHelper}
              required='required'
              placeholder='Appointment Time'
              controlId='formGroupAppointmentTime'
              className='col-md-4'
                />  
                 <Field
              name='gender'
              label='Gender'
              component={this.renderHelperGender}
              type='select'
              as='select'
              required='required'
              placeholder='Gender'
              controlId='formGroupGender'
              className='col-md-4'
              
              />

                 <Field
                name='AppointmentTime'
              label='Appointment Time'
              type='time'
              // min={min}
              component={this.renderHelper}
              required='required'
              placeholder='Appointment Time'
              controlId='formGroupAppointmentTime'
              className='col-md-4'
                />
          </Form.Row>
          <Form.Row>
            <Button
              className='container col-md-4 mt-5'
              type='submit'
              variant='success'
            >
              {' '}
              Book Now{' '}
            </Button>
          </Form.Row>
        </Form>
      </div>
    );
  }
}

// function mapStateToProps(state, props) {
//   return {
//     initialValues: state.getAllDoctorsProfiles.doctorsProfiles,
//     // dataFill:state.getAllDoctorsProfiles.doctorsProfiles
//   };
// }

// Decorate with reduxForm(). It will read the initialValues prop provided by connect()
BookAppointment = reduxForm({
  form: 'initializeFromState',
  enableReinitialize: true,
})(BookAppointment);

// now set initialValues using data from your store state
BookAppointment = connect(
  (state) => ({
    initialValues: _.merge(state.authReducer.authenticated, _.pick(state.getAllDoctorsProfiles.doctorsProfiles,'department','owner'))
  }),
  { load }
)(BookAppointment);

export default BookAppointment;

