import React,{Component} from 'react'
import {Field} from 'redux-form'
import {Form,Button,Alert} from 'react-bootstrap'

class  DoctorAppointmentSchedule extends Component {



 renderHelper = ({input,type,label,controlId,placeholder,required,accept,meta,error}) => {

        console.log('meta ',meta)


        return(

    <div className=' container col-md-4'>
            <Form.Group  controlId={controlId}>
            <Form.Label>{label}</Form.Label>
            <Form.Control 
           required
           {...input}
            type={type}
             placeholder={placeholder}  />
             {this.renderError(meta)}
          </Form.Group>



          </div>

        )

    }

    render(){
        const {handleSubmit} = this.props;

        return (
            <div>

<Form className='bg-secondary ' >

    <div className='row'>
        <h3 className="container text-center text-dark display-4">Doctor's Appointment Scheduler </h3>

            </div>  

             <Form.Row>
             <Field
                name="dayOfAppointment"
                type="date"
                label="Day"
                controlId="formGridAppointmentDay"
                placeholder="Day"
                component={this.renderHelper}
                />

               </Form.Row>

               <Form.Row>
               <Field
                name="timeOfAppointment"
                type="time"
                controlId="formGridAppointmentTime"
                label="Appointment Time"
                placeholder="Appointment Time"
                component={this.renderHelper}
                />
               </Form.Row>

               {/* <div><Alert >{this.props.errorMessage}</Alert></div> */}

<Form.Row>
<Button className='container col-md-4' type ="submit" variant="success"> Submit </Button> 
</Form.Row>
<br />  
</Form>

            </div>
        )

    }

}          

export default reduxForm({

    form:'D',
    validate
})(DoctorAppointmentSchedule)

// export default DoctorAppointmentSchedule
