  componentDidMount() {
    bsCustomFileInput.init();
  }

  renderError({ error, touched }) {
    if (error && touched) {
      return (
        <div className=''>
          <Alert variant='danger'>{error}</Alert>
        </div>
      );
    }
  }


  renderHelperCategory = ({
    input,
    type,
    label,
    controlId,
    className,
    placeholder,
    required,
    accept,
    meta,
    error,
    as,
    maxDate,
